body.script:
//
  const CONFIG = {
    baseUrl: 'https://eventsushi.blogspot.com',
    whatsapp: '212664070513'
  };

  let allProducts = [];
  let cart = [];

  function fetchProducts() {
    const script = document.createElement('script');
    script.src = CONFIG.baseUrl + '/feeds/posts/default?alt=json&amp;max-results=50&amp;callback=handleFeed';
    document.body.appendChild(script);
  }

  window.handleFeed = function(json) {
    const entries = json.feed.entry || [];
    allProducts = entries.map(e => {
      let price = 0, cat = "General";
      if (e.category) {
        e.category.forEach(c => {
          if (c.term.startsWith('price-')) price = c.term.split('-')[1];
          else cat = c.term;
        });
      }
      const content = e.content ? e.content.$t : '';
      const imgMatch = content.match(/<img[^>]+src="([^">]+)"/);
      return {
        id: e.id.$t.split('post-')[1],
        title: e.title.$t,
        category: cat,
        price: parseFloat(price) || 0,
        image: imgMatch ? imgMatch[1] : (e.media$thumbnail ? e.media$thumbnail.url.replace('s72-c', 's600') : '')
      };
    });
    renderFilters();
    renderProducts(allProducts);
  };

  function renderFilters() {
    const cats = ['All', ...new Set(allProducts.map(p => p.category))];
    document.getElementById('filterBar').innerHTML = cats.map(c => 
      `<button class="filter-btn ${c==='All'?'active':''}" onclick="filterBy('${c}', this)">${c}</button>`
    ).join('');
  }

  window.filterBy = function(cat, btn) {
    document.querySelectorAll('.filter-btn').forEach(b => b.classList.remove('active'));
    btn.classList.add('active');
    renderProducts(cat === 'All' ? allProducts : allProducts.filter(p => p.category === cat));
  };

  function renderProducts(items) {
    document.getElementById('shopContent').innerHTML = items.map(p => `
      <div class="menu-item">
        <img src="${p.image}" class="card-img" />
        <div class="card-body p-4 d-flex flex-column">
          <h5 class="fw-bold">${p.title}</h5>
          <div class="price mb-3">${p.price} DH</div>
          <button class="order-btn mt-auto" onclick="addToCart('${p.id}')">Add to Cart</button>
        </div>
      </div>
    `).join('');
  }

  window.addToCart = function(id) {
    const p = allProducts.find(x => x.id === id);
    const existing = cart.find(x => x.id === id);
    if (existing) existing.qty++;
    else cart.push({...p, qty: 1});
    updateCartUI();
    if (!document.getElementById('cartDrawer').classList.contains('open')) toggleCart();
  };

  function updateCartUI() {
    document.getElementById('cartCount').innerText = cart.reduce((a, b) => a + b.qty, 0);
    const total = cart.reduce((a, b) => a + (b.price * b.qty), 0);
    document.getElementById('cartTotal').innerText = total + " DH";

    document.getElementById('cartItems').innerHTML = cart.map(i => `
      <div class="d-flex justify-content-between align-items-center mb-3 border-bottom border-secondary pb-2">
        <div>
          <div class="fw-bold">${i.title}</div>
          <small class="text-primary">${i.qty} x ${i.price} DH</small>
        </div>
        <button class="btn btn-sm btn-outline-danger" onclick="removeFromCart('${i.id}')"><i class="bi bi-trash"></i></button>
      </div>
    `).join('');
  }

  window.removeFromCart = function(id) {
    cart = cart.filter(x => x.id !== id);
    updateCartUI();
  };

  window.toggleCart = function() {
    document.getElementById('cartDrawer').classList.toggle('open');
    document.getElementById('cartOverlay').classList.toggle('show');
  };

  window.checkout = function() {
    if (!cart.length) return alert("Your selection is empty!");
    const summary = cart.map(i => `${i.qty}x ${i.title}`).join(', ');
    const total = cart.reduce((a, b) => a + (b.price * b.qty), 0);
    const msg = encodeURIComponent(`*NEW ORDER FROM SITE*\n------------------\n${summary}\n------------------\n*Total:* ${total} DH`);
    window.open(`https://wa.me/${CONFIG.whatsapp}?text=${msg}`, '_blank');
  };

  fetchProducts();
//
